import React, { useState } from 'react';
import '../../App.css';
import '../css/HomeSection.css';
import ReactTyped from 'react-typed';

function HomeSection() {
  const idElement = document.getElementById('about');
  const [hovering, setHovering] = useState(false);

  window.addEventListener('scrollToElement', (event) => { const { idElement } = event.detail; })

  const scrollDown = () => {
    if (idElement) {
      idElement.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const signAnimation = () => {
      setHovering(true);
  };

  const signStopped = () => {
    setHovering(false);
  };

  return (
    <div className='hero-container'>
      <h1> PORTFOLIO </h1>
      <h2 content='>>'> <ReactTyped strings={["Designed by bagaco"]} typeSpeed={100} backSpeed={100} backDelay={3000} cursorChar='/>' loop style={{color: 'green'}}/> </h2>
      <div className={`hero-btns ${hovering ? 'hovering' : ''}`}>
        <i className="fas fa-arrow-down" size="2xl" style={{ color: "#ffffff" }} onClick={scrollDown} onMouseEnter={signAnimation} onMouseLeave={signStopped} />
      </div>
    </div>
  );
}

export default HomeSection;
